if(!function_exists('reales_setup_permalinks')): 
    function reales_setup_permalinks() {
        check_ajax_referer('theme_setup_ajax_nonce', 'security');

        global $wp_rewrite;

        $wp_rewrite->set_permalink_structure('/%postname%/');
        $wp_rewrite->flush_rules();

        echo json_encode(array('setup'=>true));
        exit();

        die;
    }
endif;
add_action('wp_ajax_nopriv_reales_setup_permalinks', 'reales_setup_permalinks');
add_action('wp_ajax_reales_setup_permalinks', 'reales_setup_permalinks');

if(!function_exists('reales_setup_reading_pages')): 
    function reales_setup_reading_pages() {
        check_ajax_referer('theme_setup_ajax_nonce', 'security');

        update_option('show_on_front', 'page');

        $home_page = get_page_by_title('Home');
        $blog_page = get_page_by_title('Blog');

        if(isset($home_page->ID)) {
            update_option('page_on_front', $home_page->ID);
        }

        if(isset($blog_page->ID)) {
            update_option('page_for_posts', $blog_page->ID);
        }

        echo json_encode(array('setup'=>true));
        exit();

        die;
    }
endif;
add_action('wp_ajax_nopriv_reales_setup_reading_pages', 'reales_setup_reading_pages');
add_action('wp_ajax_reales_setup_reading_pages', 'reales_setup_reading_pages');

if(!function_exists('reales_setup_menu')): 
    function reales_setup_menu() {
        check_ajax_referer('theme_setup_ajax_nonce', 'security');

        $demo_menus = array(
            'primary' => __('Top menu', 'realeswp'),
            'leftside' => __('Leftside menu', 'realeswp'),
        );

        $locations  = get_theme_mod('nav_menu_locations');
        $ep_menus   = wp_get_nav_menus();

        $menu_conf  = $demo_menus;

        if(!empty($ep_menus) && !empty($menu_conf)) {
            foreach($ep_menus as $ep_menu) {
                if(is_object($ep_menu) && in_array($ep_menu->name, $menu_conf)) {
                    $key = array_search($ep_menu->name, $menu_conf);

                    if($key !== false) {
                        $locations[$key] = $ep_menu->term_id;
                    }
                }
            }
        }

        set_theme_mod('nav_menu_locations', $locations);

        echo json_encode(array('setup'=>true));
        exit();

        die;
    }
endif;
add_action('wp_ajax_nopriv_reales_setup_menu', 'reales_setup_menu');
add_action('wp_ajax_reales_setup_menu', 'reales_setup_menu');

