if(!function_exists('reales_import_widgets_parse_data')): 
    function reales_import_widgets_parse_data($import_array) {
        $sidebars_data = $import_array[0];
        $widget_data   = $import_array[1];

        $current_sidebars = get_option('sidebars_widgets');
        $new_widgets      = array();

        foreach($sidebars_data as $import_sidebar => $import_widgets) :
            $current_sidebars[$import_sidebar] = array();

            foreach($import_widgets as $import_widget) :
                $title               = trim(substr($import_widget, 0, strrpos($import_widget, '-')));
                $index               = trim(substr($import_widget, strrpos($import_widget, '-') + 1));
                $current_widget_data = get_option('widget_' . $title);
                $new_widget_name     = reales_get_new_widget_name($title, $index);
                $new_index           = trim(substr($new_widget_name, strrpos($new_widget_name, '-') + 1));

                if (!empty($new_widgets[$title]) && is_array($new_widgets[$title])) {
                    while (array_key_exists($new_index, $new_widgets[$title])) {
                        $new_index++;
                    }
                }

                $current_sidebars[$import_sidebar][] = $title . '-' . $new_index;

                if(array_key_exists($title, $new_widgets)) {
                    $new_widgets[$title][$new_index] = $widget_data[$title][$index];

                    if(!empty($new_widgets[$title]['_multiwidget'])) {
                        $multiwidget = $new_widgets[$title]['_multiwidget'];
                        unset($new_widgets[$title]['_multiwidget']);
                        $new_widgets[$title]['_multiwidget'] = $multiwidget;
                    } else {
                        $new_widgets[$title]['_multiwidget'] = null;
                    }
                } else {
                    $current_widget_data[$new_index] = $widget_data[$title][$index];

                    if(!empty($current_widget_data['_multiwidget'])) {
                        $current_multiwidget = $current_widget_data['_multiwidget'];
                        $new_multiwidget     = $widget_data[$title]['_multiwidget'];
                        $multiwidget         = ($current_multiwidget != $new_multiwidget) ? $current_multiwidget : 1;
                        unset($current_widget_data['_multiwidget']);
                        $current_widget_data['_multiwidget'] = $multiwidget;
                    } else {
                        $current_widget_data['_multiwidget'] = null;
                    }

                    $new_widgets[$title] = $current_widget_data;
                }
            endforeach;
        endforeach;

        if(isset($new_widgets) && isset($current_sidebars)) {
            update_option('sidebars_widgets', $current_sidebars);

            foreach($new_widgets as $title => $content) {
                update_option('widget_' . $title, $content);
            }
        }
    }
endif;

if(!function_exists('reales_get_new_widget_name')): 
    function reales_get_new_widget_name($widget_name, $widget_index) {
        $current_sidebars = get_option('sidebars_widgets');
        $all_widget_array = array();

        foreach($current_sidebars as $sidebar => $widgets) {
            if(!empty($widgets) && is_array($widgets) && $sidebar != 'wp_inactive_widgets') {
                foreach($widgets as $widget) {
                    $all_widget_array[] = $widget;
                }
            }
        }

        while(in_array($widget_name . '-' . $widget_index, $all_widget_array)) {
            $widget_index ++;
        }

        $new_widget_name = $widget_name . '-' . $widget_index;

        return $new_widget_name;
    }
endif;

